int meanVec(int vec[], int dim) {
    int mean = 0, sum = 0;
    int i;
    for (i = 0; i < dim; i++) {
        sum = sum + vec[i];
    }
    mean = sum / dim;
    return mean;
}

int meanMat(int dim, int mat[]) {
    int mean = 0, sum = 0;
    int i, j;
    for (i = 0; i < dim; i++) {
        for (j = 0; j < dim; j++) {
            sum = sum + mat[i][j];
        }
    }
    mean = sum / (dim * dim);
    return mean;
}
